<?php

namespace TGWF\Greencheck\Repository;

use Doctrine\Bundle\DoctrineBundle\Repository\ServiceEntityRepository;
use Doctrine\Persistence\ManagerRegistry;
use TGWF\Greencheck\Entity\GreencheckAs;

/**
 * GreencheckAsRepository.
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class GreencheckAsRepository extends ServiceEntityRepository
{
    public function __construct(ManagerRegistry $registry)
    {
        parent::__construct($registry, GreencheckAs::class);
    }

    public function findAllActive($provider)
    {
        $qb = $this->createQueryBuilder('i');
        $query = $qb->where(
            $qb->expr()->eq('i.hostingprovider', ':provider')
                )->setParameters(
                    [
                                        'provider' => $provider,
                                      ]
                                )
                ->orderBy('i.asn')
                ->getQuery();

        return $query->getResult();
    }

    /**
     * Check if AS is in the database as green.
     *
     * @param int $as
     *
     * @return array|null
     */
    public function checkAs($as)
    {
        $qb = $this->createQueryBuilder('i');
        $query = $qb
            ->where($qb->expr()->eq('i.asn', ':as'))
            ->andWhere('i.active = 1')
            ->setParameters(['as' => $as])
            ->getQuery();
        $query->useResultCache(true);
        $query->setResultCacheLifetime(3600);

        return $query->getOneOrNullResult();
    }
}
